mapper.select:
		SELECT * FROM fp_article
			LEFT JOIN fp_user ON fp_article.writer = fp_user.email
    		WHERE id = #{id};

mapper.update:
UPDATE fp_article
	SET category=#{category}, title=#{title}, content=#{content}, time = now(),
		filename=#{filename}, filetype=#{filetype}, fileurl=#{fileurl}
	WHERE id = #{id};
